"use client";
import React, { useState } from "react";
import {
  BlurEffect,
  FadeEffect,
  MouseTrailer,
  MouseTrailerProps,
} from "@/components/content/mouse-trailer";

interface Settings {
  trailLength: number;
  speed: number;
  blur: BlurEffect;
  fade: FadeEffect;
  useCustomImage: boolean;
  color: string;
  size: number;
  springPreset: MouseTrailerProps["springPreset"];
}

const MouseTrailerDemo: React.FC = () => {
  const [settings, setSettings] = useState<Settings>({
    trailLength: 20,
    speed: 3,
    blur: { enabled: true, intensity: 5 },
    fade: { enabled: true, startOpacity: 1, endOpacity: 0.1 },
    useCustomImage: false,
    color: "#3B82F6",
    size: 12,
    springPreset: "very-tight",
  });

  const customImage = settings.useCustomImage
    ? {
        src: "https://images.unsplash.com/photo-1618336753974-aae8e04506aa",
        width: 200,
      }
    : undefined;

  return (
    <>
      <MouseTrailer
        trailLength={settings.trailLength}
        speed={settings.speed}
        blur={settings.blur}
        fade={settings.fade}
        customImage={customImage}
        color={settings.color}
        size={settings.size}
        springPreset={settings.springPreset}
      />
      <div className="bg-primary-foreground mx-auto my-5 max-w-md rounded-lg px-5 py-2.5 shadow-2xl">
        {/* <div className="place-items-center"> */}
        <p className="text-primary font-instrument-serif py-2 text-center text-xl tracking-wide underline">
          Configure the way you like
        </p>
        {/* </div> */}
        <div className="space-y-3 font-sans">
          <div>
            <label className="text-primary mb-1 block font-mono text-sm font-medium">
              Trail Length: {settings.trailLength}
            </label>
            <input
              type="range"
              min="1"
              max="50"
              value={settings.trailLength}
              onChange={(e) => setSettings({ ...settings, trailLength: parseInt(e.target.value) })}
              className="w-full"
            />
          </div>
          <div>
            <label className="text-primary mb-1 block font-mono text-sm font-medium">
              Speed: {settings.speed}
            </label>
            <input
              type="range"
              min="1"
              max="10"
              value={settings.speed}
              onChange={(e) => setSettings({ ...settings, speed: parseInt(e.target.value) })}
              className="w-full"
            />
          </div>
          <div>
            <label className="text-primary mb-1 block font-mono text-sm font-medium">
              Size: {settings.size}px
            </label>
            <input
              type="range"
              min="4"
              max="48"
              value={settings.size}
              onChange={(e) => setSettings({ ...settings, size: parseInt(e.target.value) })}
              className="w-full"
            />
          </div>
          <div>
            <label className="text-primary mb-1 block font-mono text-sm font-medium">Color</label>
            <input
              type="color"
              value={settings.color}
              onChange={(e) => setSettings({ ...settings, color: e.target.value })}
              className="h-8 w-full cursor-pointer rounded"
            />
          </div>
          {settings.useCustomImage && (
            <div>
              <label className="text-primary mb-1 block font-mono text-sm font-medium">
                Spring Preset
              </label>
              <select
                value={settings.springPreset as string}
                onChange={(e) =>
                  setSettings({
                    ...settings,
                    springPreset: e.target.value as Settings["springPreset"],
                  })
                }
                className="w-full rounded bg-gray-700 p-2 text-white"
              >
                <option value="very-tight">Very Tight</option>
                <option value="controlled-snap">Controlled Snap</option>
                <option value="smooth-follow">Smooth Follow</option>
                <option value="strict-controlled">Strict Controlled</option>
              </select>
            </div>
          )}
          <div className="flex items-center gap-4">
            <div className="flex items-center">
              <input
                type="checkbox"
                id="blur"
                checked={settings.blur.enabled}
                onChange={(e) =>
                  setSettings({
                    ...settings,
                    blur: { ...settings.blur, enabled: e.target.checked },
                  })
                }
                className="mr-2"
              />
              <label htmlFor="blur" className="text-primary font-mono text-sm">
                Blur Effect
              </label>
            </div>
            <div className="flex items-center">
              <input
                type="checkbox"
                id="fade"
                checked={settings.fade.enabled}
                onChange={(e) =>
                  setSettings({
                    ...settings,
                    fade: { ...settings.fade, enabled: e.target.checked },
                  })
                }
                className="mr-2"
              />
              <label htmlFor="fade" className="text-primary font-mono text-sm">
                Fade Effect
              </label>
            </div>
            <div className="flex items-center">
              <input
                type="checkbox"
                id="customImage"
                checked={settings.useCustomImage}
                onChange={(e) =>
                  setSettings({
                    ...settings,
                    useCustomImage: e.target.checked,
                  })
                }
                className="mr-2"
              />
              <label htmlFor="customImage" className="text-primary font-mono text-sm">
                Use Image
              </label>
            </div>
          </div>
          {settings.blur.enabled && (
            <div>
              <label className="text-primary mb-1 block font-mono text-sm font-medium">
                Blur Intensity: {settings.blur.intensity}
              </label>
              <input
                type="range"
                min="1"
                max="20"
                value={settings.blur.intensity}
                onChange={(e) =>
                  setSettings({
                    ...settings,
                    blur: { ...settings.blur, intensity: parseInt(e.target.value) },
                  })
                }
                className="w-full"
              />
            </div>
          )}
          {settings.fade.enabled && (
            <div className="space-y-2">
              <div>
                <label className="text-primary mb-1 block font-mono text-sm font-medium">
                  Start Opacity: {settings.fade.startOpacity}
                </label>
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={settings.fade.startOpacity}
                  onChange={(e) =>
                    setSettings({
                      ...settings,
                      fade: { ...settings.fade, startOpacity: parseFloat(e.target.value) },
                    })
                  }
                  className="w-full"
                />
              </div>
              <div>
                <label className="text-primary mb-1 block font-mono text-sm font-medium">
                  End Opacity: {settings.fade.endOpacity}
                </label>
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={settings.fade.endOpacity}
                  onChange={(e) =>
                    setSettings({
                      ...settings,
                      fade: { ...settings.fade, endOpacity: parseFloat(e.target.value) },
                    })
                  }
                  className="w-full"
                />
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default MouseTrailerDemo;

import { cn } from "@/lib/utils";
import React, { useEffect, useRef } from "react";

export interface TrailPoint {
  x: number;
  y: number;
}

export interface BlurEffect {
  enabled: boolean;
  intensity: number;
}

export interface FadeEffect {
  enabled: boolean;
  startOpacity: number;
  endOpacity: number;
}

export interface CustomImage {
  src: string;
  width: number;
  height?: number;
}

export interface SpringPreset {
  stiffness: number;
  damping: number;
  mass: number;
}

export interface MouseTrailerProps {
  trailLength?: number;
  speed?: number;
  blur?: BlurEffect;
  fade?: FadeEffect;
  customImage?: CustomImage;
  color?: string;
  size?: number;
  className?: string;
  springPreset?:
    | "very-tight"
    | "controlled-snap"
    | "smooth-follow"
    | "strict-controlled"
    | SpringPreset;
}

export const MouseTrailer: React.FC<MouseTrailerProps> = ({
  trailLength = 20,
  speed = 3,
  blur = { enabled: false, intensity: 5 },
  fade = { enabled: false, startOpacity: 1, endOpacity: 0.1 },
  customImage,
  color = "#3B82F6",
  size = 12,
  springPreset = "strict-controlled",
  className,
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const trailRef = useRef<TrailPoint[]>([]);
  const mousePosRef = useRef<TrailPoint>({ x: 0, y: 0 });
  const imagePosRef = useRef<TrailPoint>({ x: 0, y: 0 });
  const velocityRef = useRef<TrailPoint>({ x: 0, y: 0 });
  const animationFrameRef = useRef<number | null>(null);
  const imageRef = useRef<HTMLImageElement | null>(null);

  const presets: Record<string, SpringPreset> = {
    "very-tight": { stiffness: 0.3, damping: 2, mass: 0.2 },
    "controlled-snap": { stiffness: 0.2, damping: 0.8, mass: 0.7 },
    "smooth-follow": { stiffness: 0.15, damping: 0.7, mass: 1.0 },
    "strict-controlled": { stiffness: 0.15, damping: 5, mass: 0.5 },
  };

  const { stiffness, damping, mass } =
    typeof springPreset === "string" ? presets[springPreset] : springPreset;

  useEffect(() => {
    if (customImage) {
      const img = new Image();
      img.src = customImage.src;
      img.onload = () => {
        imageRef.current = img;
      };
    } else {
      imageRef.current = null;
    }
  }, [customImage?.src]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const handleMouseMove = (e: MouseEvent) => {
      mousePosRef.current = { x: e.clientX, y: e.clientY };
    };

    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      trailRef.current = [];
      imagePosRef.current = { x: 0, y: 0 };
      velocityRef.current = { x: 0, y: 0 };
    };

    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("resize", handleResize);

    let lastTime = 0;
    const updateInterval = 1000 / (60 * speed);

    const animate = (timestamp: number) => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      if (imageRef.current && customImage) {
        // Spring-based motion for single image
        const dx = mousePosRef.current.x - imagePosRef.current.x;
        const dy = mousePosRef.current.y - imagePosRef.current.y;

        // Calculate acceleration based on spring force
        const ax = (stiffness * dx - damping * velocityRef.current.x) / mass;
        const ay = (stiffness * dy - damping * velocityRef.current.y) / mass;

        // Update velocity and position
        velocityRef.current.x += ax / 60; // ~(60 FPS)
        velocityRef.current.y += ay / 60;
        imagePosRef.current.x += velocityRef.current.x;
        imagePosRef.current.y += velocityRef.current.y;

        // image at top-left corner of mouse position
        ctx.drawImage(
          imageRef.current,
          imagePosRef.current.x,
          imagePosRef.current.y,
          customImage.width,
          customImage.height || customImage.width
        );
      } else {
        if (timestamp - lastTime >= updateInterval) {
          trailRef.current.push({ ...mousePosRef.current });
          if (trailRef.current.length > trailLength) {
            trailRef.current.shift();
          }
          lastTime = timestamp;
        }

        trailRef.current.forEach((point, index) => {
          const progress = 1 - index / (trailRef.current.length - 1);
          const opacity = fade.enabled
            ? fade.startOpacity + (fade.endOpacity - fade.startOpacity) * progress
            : 1;
          const blurAmount = blur.enabled ? blur.intensity * (1 - progress) : 0;

          ctx.save();
          ctx.globalAlpha = opacity;
          if (blur.enabled) ctx.filter = `blur(${blurAmount}px)`;

          ctx.beginPath();
          ctx.arc(point.x, point.y, size / 2, 0, Math.PI * 2);
          ctx.fillStyle = color;
          ctx.fill();

          ctx.restore();
        });
      }

      animationFrameRef.current = requestAnimationFrame(animate);
    };

    animationFrameRef.current = requestAnimationFrame(animate);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("resize", handleResize);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [trailLength, speed, blur, fade, customImage, color, size, stiffness, damping, mass]);

  return (
    <canvas ref={canvasRef} className={cn("pointer-events-none fixed inset-0 z-50", className)} />
  );
};

MouseTrailer.displayName = "MouseTrailer";

// DevelopedBy: AetherUI
