import { TiltedCarousel } from "@/content/tilted-carousel";

function TiltedCarouselDemo2() {
  const images = [
    "https://images.unsplash.com/photo-1519681393784-d120267933ba?auto=format&fit=crop&w=800&q=80",
    "https://images.unsplash.com/photo-1525253086316-d0c936c814f8?auto=format&fit=crop&w=800&q=80",
    "https://images.unsplash.com/photo-1756894256833-934a85a42df9?q=80&w=687&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    "https://images.unsplash.com/photo-1482192505345-5655af888cc4?auto=format&fit=crop&w=800&q=80",
    "https://images.unsplash.com/photo-1499951360447-b19be8fe80f5?auto=format&fit=crop&w=800&q=80",
    "https://images.unsplash.com/photo-1500534623283-312aade485b7?auto=format&fit=crop&w=800&q=80",
    "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=800&q=80",
    "https://images.unsplash.com/photo-1755090281929-2f027ee94c98?q=80&w=687&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
  ];

  return (
    <div className="relative h-screen w-screen overflow-hidden">
      <div className="absolute inset-0 z-0">
        <TiltedCarousel images={images} pauseOnHover preset="dramatic" className="bg-cyan-200" />
        <div className="absolute inset-0 z-10 bg-black/50 shadow-[inset_0_0_100px_40px_rgba(0,0,0,0.6)]" />
      </div>

      <div className="relative z-20 flex h-full w-full items-center justify-center px-6 text-center">
        <div className="mx-auto max-w-2xl rounded-xl bg-black/30 p-8 shadow-xl backdrop-blur-md md:p-12">
          <h1 className="font-bricolage-grotesque mb-4 text-4xl text-white drop-shadow-md md:text-5xl">
            Aether/<span className="text-gradient">ui</span>
          </h1>
          <p className="text-lg font-light text-white/90 md:text-xl">
            Stunning hero section powered by{" "}
            <span className="font-bricolage-grotesque font-medium text-white">TiltedCarousel</span>
          </p>
        </div>
      </div>
    </div>
  );
}

export default TiltedCarouselDemo2;

"use client";
import { cn } from "@/lib/utils";

export interface TiltedCarouselProps {
  images: string[];
  className?: string;
  rowClassName?: string;
  imageClassName?: string;
  speed?: number;
  rows?: number;
  multiplier?: number;
  direction?: "left" | "right" | "alternate";
  pauseOnHover?: boolean;
  preset?: "default" | "compact" | "spacious" | "dramatic";
}

const PRESETS = {
  default: {
    className: "h-[800px]",
    rowClassName: "gap-4",
    imageClassName: "h-64 w-48 rounded-2xl shadow-2xl",
  },
  compact: {
    className: "h-[600px]",
    rowClassName: "gap-2",
    imageClassName: "h-48 w-36 rounded-xl shadow-lg",
  },
  spacious: {
    className: "h-[1000px]",
    rowClassName: "gap-6",
    imageClassName: "h-80 w-60 rounded-3xl shadow-[0_20px_50px_rgba(0,0,0,0.5)]",
  },
  dramatic: {
    className: "h-[900px]",
    rowClassName: "gap-5",
    imageClassName: "h-72 w-52 rounded-2xl shadow-[0_25px_60px_rgba(0,0,0,0.6)]",
  },
};

export function TiltedCarousel({
  images,
  className,
  rowClassName,
  imageClassName,
  speed = 30,
  rows = 4,
  multiplier = 6,
  direction = "alternate",
  pauseOnHover = false,
  preset = "default",
}: TiltedCarouselProps) {
  if (!images || !images.length) {
    return (
      <div className="flex h-[800px] w-full items-center justify-center bg-black">
        <p className="text-white/50">No images provided</p>{" "}
      </div>
    );
  }

  const presetConfig = PRESETS[preset];
  const finalClassName = cn(presetConfig.className, className);
  const finalRowClassName = cn(presetConfig.rowClassName, rowClassName);
  const finalImageClassName = cn(presetConfig.imageClassName, imageClassName);
  const duplicatedImages = Array.from({ length: multiplier }, () => images).flat();
  const imagesPerRow = Math.ceil(duplicatedImages.length / rows);
  const imageRows = Array.from({ length: rows }, (_, rowIndex) =>
    duplicatedImages.slice(rowIndex * imagesPerRow, (rowIndex + 1) * imagesPerRow)
  );

  const getAnimationName = () => {
    switch (direction) {
      case "left":
        return "scroll-left";
      case "right":
        return "scroll-right";
      case "alternate":
        return "scroll-bidirectional";
      default:
        return "scroll-bidirectional";
    }
  };

  const animationName = getAnimationName();

  return (
    <div
      className={cn("relative h-screen w-full overflow-hidden bg-black", finalClassName)}
      style={{
        perspective: "1000px",
      }}
    >
      <div
        className="absolute inset-0 -top-60 flex flex-col gap-4 py-8"
        style={{
          transform: "rotateX(25deg) rotateY(-15deg) rotateZ(5deg)",
          transformStyle: "preserve-3d",
        }}
      >
        {imageRows.map((rowImages, rowIndex) => (
          <div
            key={rowIndex}
            className={cn(
              "flex shrink-0",
              finalRowClassName,
              pauseOnHover && "hover:![animation-play-state:paused]"
            )}
            style={{
              animation:
                speed > 0
                  ? `${animationName} ${speed}s linear infinite ${direction === "alternate" ? "alternate" : ""}`
                  : "none",
              animationDelay: `${rowIndex * -3}s`,
            }}
          >
            {[...rowImages, ...rowImages].map((image, imageIndex) => (
              <div
                key={`${rowIndex}-${imageIndex}`}
                className={cn("relative shrink-0 overflow-hidden", finalImageClassName)}
                style={{ transform: "translateZ(20px)" }}
              >
                <img
                  src={image}
                  alt={`Carousel image ${imageIndex + 1}`}
                  className="h-full w-full object-cover"
                />
              </div>
            ))}
          </div>
        ))}
      </div>

      <style jsx>{`
        @keyframes scroll-left {
          from {
            transform: translateX(0);
          }
          to {
            transform: translateX(-50%);
          }
        }

        @keyframes scroll-right {
          from {
            transform: translateX(-50%);
          }
          to {
            transform: translateX(0);
          }
        }

        @keyframes scroll-bidirectional {
          from {
            transform: translateX(0);
          }
          to {
            transform: translateX(-50%);
          }
        }
      `}</style>
    </div>
  );
}

